import java.util.*;public class Main {    public static void main(String args[]) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        for(int i=0;i<t;i++) {            int n = sc.nextInt();            int arr[] = new int[n];            for(int j=0;j<n;j++) {                arr[j] = sc.nextInt();            }            int neg = 0;            for(int k=0;k<n;k++) {                if(arr[k]<0) {                    neg++;                }            }            int zero=0;            for(int m=0;m<n;m++) {                if(arr[m]==0) {                    zero++;                }            }            int count =0;            if(neg%2!=0) {                count+=2;            }            count+=zero;            System.out.println(count);        }    }}